.navigation {
  grid-area: navigation;
  border-right: 1px solid @lightgray;
  writing-mode: vertical-rl;
  text-orientation: mixed;
  color: @brown-lighter;
  text-transform: uppercase;
  letter-spacing: 1px;
  padding: 20px;
  font: 16px 'Playfair Display', serif;
  text-align: center;
}

.pie-image {
  grid-area: pie-image;
  background: url('https://meowlivia.s3.us-east-2.amazonaws.com/codepen/Waitress/GettingOutOfMudPie.png')
    no-repeat left center / cover;
}

.pie-name {
  font: 900 45px Raleway;
  text-transform: uppercase;
  margin: 20px 0 5px;
  letter-spacing: 2px;
  color: @brown;
  position: relative;
}

.pie-serving {
  font: 400 16px Raleway;
  color: @brown;
  margin: 20px 0;
  max-width: 300px;
  svg {
    width: 40px;
    path {
      fill: @brown;
    }
  }
  p {
    display: inline-block;
    vertical-align: top;
    margin: 12px 6px 6px;
  }
}

::-webkit-scrollbar {
  width: 10px;
}

::-webkit-scrollbar-track {
  background: #f1f1f1;
}

::-webkit-scrollbar-thumb {
  background: @brown-lighter;
}

::-webkit-scrollbar-thumb:hover {
  background: @brown-light;
}

.pie-recipe {
  grid-area: pie-recipe;
  color: #999;
  overflow-y: auto;
  overflow-x: hidden;
  margin: 5px;
  max-height: 100%;
  padding: 50px 60px;
  display: grid;
  grid-gap: 20px;
  grid-template-columns: 30px auto;
  grid-template-rows: repeat(4, max-content);
  line-height: 1.6;
  &__title {
    grid-column: span 2;
  }
  &__ingredients {
    grid-column: span 2;
    display: flex;
    flex-wrap: wrap;
    &__item {
      flex: 1;
      padding: 0 40px 0 0;
      h4 {
        font: italic 20px/1.1 'Playfair Display', serif;
        color: @brown-light;
        margin: 0 0 10px;
        margin-top: 30px;
      }
      ul {
        margin: 0;
        padding: 0;
        list-style: none;
        li {
          padding: 5px 0;
        }
      }
    }
  }
  &__number {
    font: italic 30px/1 'Playfair Display', serif;
    color: @brown-light;
    place-self: center;
    margin-top: -15px;
  }
  &__steps {
    padding-right: 35px;
  }
  &__subtitle {
    font: italic 30px/1 'Playfair Display', serif;
    color: @brown;
    margin: 45px 0 10px;
    grid-column: span 2;
  }
  &__tips {
    grid-column: span 2;
    margin-top: 65px;
    position: relative;
    border: 2px solid @brown;
    box-shadow: inset 0 0 0 2px #fff, inset 0 0 0 4px @brown;
    text-align: center;
    padding: 35px;
    svg {
      display: inline-block;
      padding: 10px;
      position: absolute;
      margin: auto;
      left: 0;
      right: 0;
      top: -40px;
      width: auto;
      background: #fff;
      width: 60px;
      fill: @brown;
    }
    h4 {
      font: 900 24px Raleway;
      text-transform: uppercase;
      margin: 10px 0 15px;
      color: @brown-dark;
    }
  }
}
